import { TextField } from '@material-ui/core';
import React, { useState } from 'react';
import fetchAPI from '../api/index';

function Comments(props) {
    const {contact} = props
    const [commentList, setCommentList] =useState([])
    const [newComment, setNewComment]= useState('')

    return (
        <>
            <p><strong>Comments: </strong> {contact.comments? null : 'None'}</p>
            {contact.comments.length < 0? null : (contact.comments).map((comment, index) => {
                return (<div key={index} >
                    <p> - {comment.content}</p>
                    <button>Delete Comment</button>
                </div>
                 )
            })}
            <input type={TextField} placeholder="Type New Comment" value={newComment} onSubmit= {(event) => {setNewComment(event.target.value)}}></input>
            <button
                onClick={async (event) => {
                    event.preventDefault()
                    fetchAPI(`https://univ-contact-book.herokuapp.com/api/contacts/${contact.id}/comments`, "POST", {
                    content: newComment,
                    })
                    .then(function (data) {
                        (contact.comments).push(data.comment);
                    })
                    .then(
                        setNewComment('')
                    )
                    .catch(console.error);
                }}
            >Add Comment</button>
        </>
    )

}

export default Comments
