import React, { useState } from 'react';

function Comments(props) {
    const {contact} = props
    console.log(contact)
    return (
        <>
            <p><strong>Comments: </strong> {contact.comment.length = 0? 'NONE' : `${contact.comment.length}`}</p>
            {contact.comment.length = 0? null : (contact.comment).map((comment, index) => {
                return (<>
                <p> - {comment.content}</p> 
                <button>Delete</button>
                </>)
            })}
        </>
    )

}

export default Comments
