import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import Input from '@material-ui/core/Input';
import MaskedInput from 'react-text-mask';

function rand() {
  return Math.round(Math.random() * 20) - 10;
}

function getModalStyle() {

  return {
    top: `50%`,
    left: `50%`,
    transform: `translate(-50%, -50%)`,
  };
}

const useStyles = makeStyles((theme) => ({
  paper: {
    position: 'absolute',
    width: '80vw',
    backgroundColor: theme.palette.background.paper,
    border: '2px solid #000',
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
  },
}));

export default function SimpleModal() {
  const classes = useStyles();
  const [modalStyle] = React.useState(getModalStyle);
  const [open, setOpen] = React.useState(false);

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const body = (
    <div style={modalStyle} className={classes.paper}>
        <form className={classes.root} noValidate autoComplete="off">
            <Input placeholder="Name" inputProps={{ 'aria-label': 'description' }} />
            <Input placeholder="Address" inputProps={{ 'aria-label': 'description' }} />
            <Input placeholder="Phone Number"  inputProps={{ 'aria-label': 'description' }} />
            <Input placeholder="Email"  inputProps={{ 'aria-label': 'description' }} />
            <Input placeholder="Work/Personal/Other"  inputProps={{ 'aria-label': 'description' }} />
            <MaskedInput
                mask={['(', /[1-9]/, /\d/, /\d/, ')', ' ', /\d/, /\d/, /\d/, '-', /\d/, /\d/, /\d/, /\d/]}
                placeholder="Phone Number"
                className={classes.root}
            />
        </form>
    </div>
  );

  return (
    <div>
      <button type="button" onClick={handleOpen}>
        Open Modal
      </button>
      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="simple-modal-title"
        aria-describedby="simple-modal-description"
      >
        {body}
      </Modal>
    </div>
  );
}
