import React, { useState } from  "react";
import "./CONTACTS.css"
import { Accordion, AccordionDetails, AccordionSummary, Typography  } from '@material-ui/core';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import EditIcon from '@material-ui/icons/Edit';
import DeleteIcon from '@material-ui/icons/Delete';
import fetchAPI from '../api/index'
import Modal from './MODAL';

function Contacts(props) {
    const {contactList, setContactList} = props
    const [modifyContact, setModifyContact] = useState(false)
    console.log(contactList)

    return (contactList.map((contact, index) => {
        return <Accordion key={index}>
            {(modifyContact === contact.id) ? <Modal open={true} />: null}
        <AccordionSummary
          expandIcon={<ExpandMoreIcon />}
          aria-controls="panel1a-content"
          id="panel1a-header"
        >
          <Typography><strong>{contact.name}</strong></Typography>
        </AccordionSummary>
        <AccordionDetails className="details">
          <Typography>
            <strong>ADDRESS: </strong>{contact.address}<br></br>
            <strong>PHONE NUMBER: </strong>{contact.phoneNumber}<br></br>
            <strong>EMAIL: </strong>{contact.email}<br></br>
            <strong>CONTACT TYPE: </strong>{contact.contactType}<br></br>
          </Typography>
          <div className="icons">
            <DeleteIcon 
                id="delete" 
                fontSize="large"
                onClick={async (event) => {
                    event.preventDefault();
                    fetchAPI(`https://univ-contact-book.herokuapp.com/api/contacts/${contact.id}`, "DELETE")
                    .then(console.log)
                    .then(
                        contactList.splice(contactList.findIndex(() => [contact]), 1),
                        setContactList([contactList]))
                    .catch(console.error);
                }}
                ></DeleteIcon>
            <EditIcon  
                id="edit" 
                fontSize="large"
                onClick={(event) => {
                    event.preventDefault();
                    setModifyContact(contact.id)
                }
                }
                ></EditIcon>
          </div>
        </AccordionDetails>
        </Accordion>

    })

    )
    }




export default Contacts
