import React from 'react';
import { useForm } from 'react-hook-form';

export default function Form() {
  const { register, handleSubmit, errors } = useForm();
  const onSubmit = data => console.log(data);
  console.log(errors);

  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <input type="text" placeholder="Name" name="Name" ref={register({required: true})} />
      <input type="text" placeholder="Address" name="Address" ref={register({required: true})} />
      <input type="tel" placeholder="PhoneNumber" name="PhoneNumber" ref={register({required: true})} />
      <input type="email" placeholder="Email" name="Email" ref={register({required: true})} />

      <input name="Contact Type" type="radio" value="Work" ref={register({ required: true })}/>
      <input name="Contact Type" type="radio" value=" Personal" ref={register({ required: true })}/>
      <input name="Contact Type" type="radio" value=" Other" ref={register({ required: true })}/>

      <input type="submit" />
    </form>
  );
}
