import React, { useState } from 'react';

function Comments(props) {
    const {contact} = props
    console.log(contact)
    return (
        <>
            <p><strong>Comments: </strong> {contact.comments.length = 0? 'NONE' : `${contact.comments.length}`}</p>
            {contact.comments.length = 0? null : (contact.comments).map((comment, index) => {
                return (<p> - {comment.content}</p> )
            })}
            <button>Delete</button>
        </>
    )

}

export default Comments
